function escapeSQL(str) {
  if (str === null || str === undefined) return 'NULL';
  return `'${String(str).replace(/'/g, "''")}'`;
}

function generateTeamsSQL() {
  const statements = ['-- Football Teams (132 FBS Programs)'];

  for (const team of FBS_TEAMS) {
    statements.push(`INSERT OR REPLACE INTO football_teams (id, name, slug, mascot, conference, division, city, state, stadium, stadium_capacity, head_coach, primary_color, secondary_color, espn_id) VALUES (${escapeSQL(team.id)}, ${escapeSQL(team.name)}, ${escapeSQL(team.slug)}, ${escapeSQL(team.mascot)}, ${escapeSQL(team.conference)}, 'FBS', ${escapeSQL(team.city)}, ${escapeSQL(team.state)}, ${escapeSQL(team.stadium)}, ${team.capacity}, ${escapeSQL(team.coach)}, ${escapeSQL(team.primary)}, ${escapeSQL(team.secondary)}, ${team.espn});`);
  }

  return statements.join('\n');
}

function generatePortalSQL() {
  const statements = ['-- Transfer Portal Players (Top 100)'];

  for (const player of TRANSFER_PORTAL) {
    statements.push(`INSERT OR REPLACE INTO football_transfer_portal (id, player_name, first_name, last_name, position, year, from_school, from_conference, to_school, to_conference, status, entry_date, commit_date, stars, stats_passing_yards, stats_rushing_yards, stats_receiving_yards, stats_tackles, stats_sacks, stats_interceptions, impact_score, notes) VALUES (${escapeSQL(player.id)}, ${escapeSQL(player.name)}, ${escapeSQL(player.name.split(' ')[0])}, ${escapeSQL(player.name.split(' ').slice(1).join(' '))}, ${escapeSQL(player.pos)}, ${escapeSQL(player.year)}, ${escapeSQL(player.from)}, ${escapeSQL(player.fromConf)}, ${player.to ? escapeSQL(player.to) : 'NULL'}, ${player.toConf ? escapeSQL(player.toConf) : 'NULL'}, ${escapeSQL(player.status)}, ${escapeSQL(player.entry)}, ${player.commit ? escapeSQL(player.commit) : 'NULL'}, ${player.stars}, ${player.passYds || 'NULL'}, ${player.rushYds || 'NULL'}, ${player.recYds || 'NULL'}, ${player.tackles || 'NULL'}, ${player.sacks || 'NULL'}, ${player.ints || 'NULL'}, ${player.impact}, ${escapeSQL(player.notes)});`);
  }

  return statements.join('\n');
}

