// Generate SEO metadata for each team
function generateSEO(team) {
  return {
    seo_title: `${team.name} Baseball | Schedule, Roster, Stats | ${team.conference}`,
    seo_description: `${team.name} baseball coverage: live scores, schedule, roster, stats, and recruiting news. Home stadium: ${team.stadium_name}. ${team.conference} baseball.`,
    seo_keywords: `${team.name}, ${team.slug} baseball, ${team.conference} baseball, ${team.city} ${team.state} college baseball, ${team.mascot || team.name} schedule`
  };
}

// Format team for SQL INSERT
function formatTeamSQL(team) {
  const seo = generateSEO(team);
  const values = [
    team.id,
    null, // espn_id
    team.name,
    team.slug,
    team.mascot || null,
    team.abbreviation,
    null, // nickname
    team.conference,
    'D1',
    team.city,
    team.state,
    team.primary_color,
    team.secondary_color,
    null, // logo_url
    team.stadium_name,
    null, // stadium_capacity
    null, // stadium_surface
    team.coach_name,
    null, // coach_years
    null, // coach_record
    null, // website_url
    team.twitter_handle,
    null, // instagram_handle
    null, // founded_year
    seo.seo_title,
    seo.seo_description,
    seo.seo_keywords,
    0, // page_views
    null // last_viewed_at
  ];

  return values.map(v => v === null ? 'NULL' : `'${String(v).replace(/'/g, "''")}'`).join(', ');
}

// Generate SQL batch inserts
function generateInsertSQL(teams, batchSize = 50) {
  const batches = [];
  for (let i = 0; i < teams.length; i += batchSize) {
    const batch = teams.slice(i, i + batchSize);
    const values = batch.map(formatTeamSQL).join('),\n(');
    batches.push(`INSERT INTO college_baseball_teams (
  id, espn_id, name, slug, mascot, abbreviation, nickname, conference, division,
  city, state, primary_color, secondary_color, logo_url, stadium_name,
  stadium_capacity, stadium_surface, coach_name, coach_years, coach_record,
  website_url, twitter_handle, instagram_handle, founded_year,
  seo_title, seo_description, seo_keywords, page_views, last_viewed_at
) VALUES
(${values});`);
  }
  return batches;
}

